    @SuppressWarnings("empty-statement")
    private void btnAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAlterarActionPerformed
        //Pegar a linha selecionada
        int linha = tableIMC.getSelectedRow();

        //Pegar o objeto da linha selecionada
        Imc imc = lista.get(linha);

        //Setar os novos dados com o que foi digitado
        imc.setNome(txtNome.getText());

        try {
            imc.setPeso(Float.valueOf(txtPeso.getText()));
            imc.setAltura(Float.valueOf(txtAltura.getText()));
        } catch (NumberFormatException numberFormatException) {
            JOptionPane.showMessageDialog(rootPane, "Valor inválido!");
        }

        //Listar novamente
        listarDados();
        //Limpar campos
        limparCampos();
    }//GEN-LAST:event_btnAlterarActionPerformed

    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        //Instanciar novo objeto
        Imc imc = new Imc();
        //Captar valores digitados
        try {
            imc.setCpf(Long.valueOf(txtCPF.getText()));
        } catch (NumberFormatException numberFormatException) {
            JOptionPane.showMessageDialog(rootPane, "CPF Inválido! Digite apenas números!");
            return;
        }

        try {
            imc.setNome(txtNome.getText());
            imc.setPeso(Float.valueOf(txtPeso.getText()));
            imc.setAltura(Float.valueOf(txtAltura.getText()));

            //Colocar valores digitados na tabela
            lista.add(imc);
            //Limpar os campos para novo input
            limparCampos();
            //Listar os dados na tabela
            listarDados();
        } catch (NumberFormatException numberFormatException) {
            JOptionPane.showMessageDialog(rootPane, "Valor inválido!");
        }


    }//GEN-LAST:event_btnCalcularActionPerformed

    private void tableIMCMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tableIMCMouseClicked
        // Pegar a linha clicada
        int linha = tableIMC.getSelectedRow();
        //Pegar a linha selecionada na lista
        Imc imc = lista.get(linha);
        //Colocar na tela os valores da linha (objeto) selecionada
        txtCPF.setText(imc.getCpf().toString());
        txtNome.setText(imc.getNome());
        txtPeso.setText(imc.getPeso().toString());
        txtAltura.setText(imc.getAltura().toString());
    }//GEN-LAST:event_tableIMCMouseClicked

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        // Pegar a linha selecionada
        int linha = tableIMC.getSelectedRow();
        // Excluir a linha selecionada
        lista.remove(linha);
        //Listar novamente os dados
        listarDados();
        //Limpar campos
        limparCampos();
    }//GEN-LAST:event_btnExcluirActionPerformed

