    //Função para limpar os campos após clique nos botões
    private void limparCampos() {
        //Deixar o valor dos campos vazio
        txtCPF.setText("");
        txtNome.setText("");
        txtPeso.setText("");
        txtAltura.setText("");
    }

    //Função para listar os dados na tabela
    private void listarDados() {
        //Montar a matriz, de acordo com a quantidade de objetos na lista
        int qtdeObjetos = lista.size();

        Object[][] dados = new Object[qtdeObjetos][6];

        for (int i = 0; i < qtdeObjetos; i++) {
            Imc imc = lista.get(i); //Pega o objeto da lista
            dados[i][0] = imc.getCpf();
            dados[i][1] = imc.getNome();
            dados[i][2] = imc.getPeso();
            dados[i][3] = imc.getAltura();
            dados[i][4] = imc.CalculaImc();
            dados[i][5] = imc.VerificaImc();

        }

        tableIMC.setModel(new javax.swing.table.DefaultTableModel(
                dados, //Inserindo matriz criada dentro do modelo
                new String[]{
                    "CPF", "Nome", "Peso", "Altura", "IMC", "Classificação"
                }
        ));
    }

